        $Form.ForeColor                 = [System.Drawing.ColorTranslator]::FromHtml("#ffffff")
    } elseif ((Get-ItemPropertyValue -Path "HKCU:\SOFTWARE\Microsoft\Windows\CurrentVersion\Themes\Personalize" -Name "AppsUseLightTheme") -eq '1') {
